function Collect_SCSMInstallationFilesInfo() {
    Start_Async -code { (Get-ChildItem -Path ((Get-ItemProperty -Path 'HKLM:\SOFTWARE\Microsoft\System Center\2010\Service Manager\Setup').InstallDirectory) -Recurse -Force | Select-Object Length, lastwritetimeutc, @{Name="Version";Expression={$_.VersionInfo.ProductVersion}}, FullName | ConvertTo-Csv -NoTypeInformation ) } -outputFileName "SCSM_Files.csv"
}